import unittest
from DBAccessSQLite import DBAccessSQLite
from EveOnline.EveDB import *


class TestEveDB(unittest.TestCase):

    DB = '../data/eve.db'
    eveDB = None

    maxDiff = None

    def setUp(self):
        self.dbAccess = DBAccessSQLite(self.DB)
        self.eveDB = EveDB(self.dbAccess)

    def tearDown(self):
        self.dbAccess.close()

